jQuery('.formresult').show();
jQuery(".loadservices").hide();
jQuery(".loadadmission").hide();
jQuery(".loadclose").hide();

var callname = jQuery('#callname').val();
var callphone = jQuery('#callphone').val();

var url ='http://majorbrains.net/[ReportinSystemWebAPI]/.php';
jQuery.ajax({
url: url,
type: "POST",
async: false,
data: ({ 'name': callname, 'mobile': callphone, 'recordtype' : "callbackrequest" }),
success: function(result){
if(result == 1){
jQuery('.loadingAppoint .loading_image').hide();
jQuery('.formresult').html('Thanks for your request ' + callname + ',<br /> will get back soon.');
jQuery(".loadservices").show();
jQuery(".loadadmission").show();
jQuery(".loadclose").show();
/* jQuery('#creq_error').css({display: 'block', color: '#00ff27'});
jQuery('#creq_error').html('Thank you for your interest!'); */	
jQuery('#creq_error').show();	
jQuery('#callname').val('');
jQuery('#callphone').val('');
}else{
jQuery('.loadingAppoint .loading_image').hide();
jQuery('.formresult').html('Oops Something Went Wrong..! Try Again');	
jQuery(".loadclose").show();
/* jQuery('#creq_error').css({display: 'block', color: 'red'});
jQuery('#creq_error').html('Something went wrong <br/> Please try again!');
jQuery('#creq_error').hide(); */
jQuery('#callname').val('');
jQuery('#callphone').val('');	

}
}
});

return false;
}
}
$(".loadingAppoint .loadclose").click(function(){
jQuery(".loadingAppoint").hide(); 
jQuery('.loadingAppoint .formresult').html('');
});
})(jQuery);	
