                        <script>
                            jQuery.noConflict();
                            (function($) {
                                $('#appointment').submit(function() {
                                    var fname = new RegExp(/^[a-zA-Z() '-]+$/);
                                    var email = new RegExp(/^[a-z][a-z0-9_\.\-]*@[a-z0-9\.\-]+\.[a-z]{2,4}$/i);
                                    var mobile = jQuery('.appmobile').val();
                                    jQuery('.apperror').html('');

                                    if (fname.test(jQuery('.appname').val()) == false) {
                                        jQuery('.apperror').html('Please enter a valid name');
                                        jQuery('.apperror').show();
                                        jQuery('.appname').focus();
                                        return false;
                                    } else if (jQuery.trim(mobile).length == 0) {
                                        $('.appmobile').mask('(000) 000-0000');
                                        jQuery('.apperror').html('Please enter your mobile number');
                                        jQuery('.apperror').show();
                                        jQuery('.appmobile').focus();
                                        return false;
                                    } else if (jQuery.trim(mobile).length < 10 || jQuery.trim(mobile).length > 12) {
                                        $('.appmobile').mask('(000) 000-0000');
                                        jQuery('.apperror').html('Please enter valid mobile number');
                                        jQuery('.apperror').show();
                                        jQuery('.appmobile').focus();
                                        return false;
                                    } else if (mobile.match(/[0-9- + ]*/) != mobile) {
                                        $('.appmobile').mask('(000) 000-0000');
                                        jQuery('.apperror').html('Please enter valid mobile number');
                                        jQuery('.apperror').show();
                                        jQuery('.appmobile').focus();
                                        return false;
                                    } else if (email.test(jQuery('.appemail').val()) == false) {
                                        jQuery('.apperror').html('Please enter a valid email ID');
                                        jQuery('.apperror').show();
                                        jQuery('.appemail').focus();
                                        return false;
                                    } else if (jQuery.trim(jQuery('.appmessage').val()).length == 0) {
                                        jQuery('.apperror').html('Please enter your comment');
                                        jQuery('.apperror').show();
                                        jQuery('.appmessage').focus();
                                        return false;
                                    } else {
                                        jQuery('.loadingAppoint').show();
                                        jQuery('.loadingAppoint .loading_image').show();
                                        jQuery('.formresult').html('Processing your data... Please wait.');
                                        jQuery('.formresult').show();
                                        jQuery(".loadservices").hide();
                                        jQuery(".loadadmission").hide();
                                        jQuery(".loadclose").hide();
                                        var name = jQuery('.appname').val();
                                        var emailid = jQuery('.appemail').val();
                                        var mobile = jQuery('.appmobile').val();
                                        var comment = jQuery('.appmessage').val();

                                        var url = '';
                                        jQuery.ajax({
                                            url: url,
                                            type: "POST",
                                            async: false,
                                            crossDomain: true,
                                            data: ({
                                                'name': name,
                                                'emailid': emailid,
                                                'mobile': mobile,
                                                'comment': comment,
                                                'recordtype': 'appointment'
                                            }),
                                            success: function(result) {
                                                if (result == 1) {
                                                    jQuery('.loadingAppoint .loading_image').hide();
                                                    jQuery('.formresult').html('Thanks for your request ' + name + ',<br /> will get back soon.');
                                                    jQuery(".loadservices").show();
                                                    jQuery(".loadadmission").show();
                                                    jQuery(".loadclose").show();
                                                    /* jQuery('#app_error').css({display: 'block', color: '#00ff27'});
                                                    jQuery('#app_error').html('Thank you for your interest!');	
                                                    jQuery('#app_error').show(); */
                                                    jQuery('.appname').val('');
                                                    jQuery('.appemail').val('');
                                                    jQuery('.appmobile').val('');
                                                    jQuery('.appmessage').val('');
                                                } else {
                                                    jQuery('.loadingAppoint .loading_image').hide();
                                                    jQuery('.formresult').html('Oops Something Went Wrong..! Try Again');
                                                    jQuery(".loadclose").show();
                                                    /* jQuery('#app_error').css({display: 'block', color: 'red'});
                                                    jQuery('#app_error').html('Something went wrong <br/> Please try again!'); */
                                                    jQuery('#app_error').hide();
                                                    jQuery('.appname').val('');
                                                    jQuery('.appemail').val('');
                                                    jQuery('.appmobile').val('');
                                                    jQuery('.appmessage').val('');
                                                }

                                            }
                                        });
                                    }

                                    return false;
                                });
                                $(".loadingAppoint .loadclose").click(function() {
                                    jQuery(".loadingAppoint").hide();
                                    jQuery('.loadingAppoint .formresult').html('');
                                });
                            })(jQuery);
                        </script>
